sp_widget.client_script:
api.controller=function(spUtil,$scope) {
  /* widget controller */
  var c = this;

  spUtil.recordWatch($scope, $scope.data.constants.TABLE_TEAM_MEMBERS, "team=" + $scope.data.team_id);



};

sp_widget.css:
table.oc-table {
