sys_script_fix.script:
var ocu = new x_snc_on_call_sc_0.OnCallUtils();
var debug = false;
var rotations = new GlideRecord(ocu.constants.TABLE_ROTATIONS);
rotations.query();
while(rotations.next()){
	let assignees = ocu.getAssigneesByRotation(rotations.getUniqueValue());

	if(assignees){
		gs.info(`Updating assignees for ${rotations.getDisplayValue('rotation')}\nOld assignees value: ${rotations.getValue(assignees)}\nNew assignees value: ${assignees}`);
		rotations.setValue('assignees',assignees);
		try {
			if(debug==false){
				rotations.update();
			}
		} catch(err){
			gs.info(`Could not update assignees for ${rotations.getDisplayValue('rotation')} due to error: ${err}`);
		}
	}
}
