sys_script_include.script:
var OnCallUtils = Class.create();
OnCallUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    initialize : function(){
		this.constants = this.getConstants();
	},
	/**
	 * getConstants(): Used in widgets, BRs, Fix Scripts, reference qualifiers, and this Script Include for constant values rather than create & use a list of system properties; needs one-time modification on app install to ensure table names are accurate
	 * returns: const array of x_ app table names
	 * TODO: add constants as needed
	 */
	getConstants : function(){
		const constants = {
			COUNT: 12,
			TABLE_ROTATIONS: 'x_snc_on_call_sc_0_on_call_rotation',
			TABLE_SHIFTS: 'x_snc_on_call_sc_0_on_call_shift',
			TABLE_TEAMS: 'x_snc_on_call_sc_0_assignment_group',
			TABLE_TEAM_MEMBERS: 'x_snc_on_call_sc_0_user',
			TABLE_HOLIDAYS: 'x_snc_on_call_sc_0_holidays'
		};

		return constants;
	},
	/**
	 * BUILD Functions
	 * 
	 * buildRotations(GlideDateTime, int): generate sequential Rotation records based on starting date and number of iterations and insert them
	 * returns: null
	 * TODO: change build to return draft, stand up different function to insert
	 * 
	 * buildTeamRoster(Team sys_id, Role string(Engineer || Manager)): generate ordered list of members based on input team and role (optional)
	 * returns: array of objects mimicking Team Member table
	 * TODO: allow for array order modification and pre-sort based on inputs
	 * 
	 * buildManagerShifts(GlideDateTime, Region string ('US' || 'IDC')): builds list of active manageres and uses buildShifts() to generate sequential manager shifts (since these are not per-team)
	 * TODO: allow for draft
	 * 
	 * buildDraftShifts(GlideDateTime, Team sys_id, string, array from buildTeamRoster): generate shifts for sequential rotations based on start value, repeating through the roster until all rotations have an associated shift - DRAFT, no insert
	 * returns: array of records viable for insertion into the Shifts table
	 * TODO:
	 * 
	 * buildShiftsFromDraft(array from buildDraftShifts): inserts the draft shifts from builDraftShifts into the Shifts table. This should serve as an example build-insert model for the other build functions.
	 * returns: null
	 * TODO:
	 * 
	 * buildShifts(GlideDateTime, Team sys_id, string, Team roster array, Boolean (unused)) - DEPRECATED: replaced by buildShiftsFromDraft(). Proceeds directly to build and insert shifts sight-unseen
	 * returns: null
	 * TODO: deprecate & remove
	 */
	buildDraftRotations : function(start,count){
		var rotations_draft = [];
		if(!count || count == ''){
			return;
		}
		if(!start || start == ''){
			start = new GlideDateTime();
		}
			for(var i=0; i<count; i++){
				//var rgr = new GlideRecord(this.constants.TABLE_ROTATIONS);
				let s_gdt = new GlideDateTime(start);
				s_gdt.addDaysLocalTime((i * 7));
				let _start = new GlideDate();
				_start.setValue(s_gdt.getLocalDate());

				let _end = new GlideDate();
				let e_gdt = new GlideDateTime(s_gdt);
				e_gdt.addDaysLocalTime(7);
				_end.setValue(e_gdt.getLocalDate());

				let from = new GlideDate();
				from = _start.getByFormat('MMM d');

				let to = new GlideDate();
				to = _end.getByFormat('MMM d');

				var name = `${from} - ${to}`;

				let rotation = {
					from:_start.toString(),
					to:_end.toString(),
					name:name
				};
				rotations_draft.push(rotation);
			}
			return rotations_draft;
	},
	buildRotationsFromDraft : function(draft){
		for(var i=0; i<draft.length; i++){
			try {
				var rgr = new GlideRecord(this.constants.TABLE_ROTATIONS);
				rgr.initialize();
				rgr.setValue('from',draft[i].from);
				rgr.setValue('to',draft[i].to);
				rgr.insert();
				gs.info(JSON.stringify(rgr));
			}
			catch(err){
				gs.info('Insertion failed due to error: '+err);
			}
		}
	},
	buildRotations : function(start,count){
		if(!count || count == ''){
			count = this.constants.COUNT;
		}
		if(!start || start == ''){
			start = new GlideDateTime();
		}
		try {
			for(var i=0; i<count; i++){
				var rgr = new GlideRecord(this.constants.TABLE_ROTATIONS);
				var s_gdt = new GlideDateTime(start);
				s_gdt.addDaysLocalTime((i * 7)+1);
				var _start = new GlideDate();
				_start.setValue(s_gdt.getLocalDate());

				var _end = new GlideDate();
				var e_gdt = new GlideDateTime(s_gdt);
				e_gdt.addDaysLocalTime(7);
				_end.setValue(e_gdt.getLocalDate());

				rgr.initialize();
				rgr.setValue('from',_start);
				rgr.setValue('to',_end);
				rgr.insert();
				gs.info(JSON.stringify(rgr));
			}
		}
		catch(err){
			gs.info('Insertion failed due to error: '+err);
		}

	},
	buildTeamRoster : function(team,role){
		console.log([team,role]);
		if(!team) {
			return gs.info('No team specified');
		}
		// if(!role){
		// 	role = 'engineer';
		// }
		/**
		 * If order, sort by order
		 * If no order, sort by Last On-Call
		 * If no Last On-Call, sort last
		 * Allow for manual update??
		 */
		var _tm_arr = [];
		var order = 1;
		var _tm = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		_tm.addQuery('active',true);
		_tm.addQuery('assignment_group',team);
		if(role){ _tm.addQuery('role',role); }
		_tm.query();
		while(_tm.next()){
			_tm_arr.push({
				id: _tm.getUniqueValue(),
				name: _tm.getDisplayValue(),
				user: _tm.getValue('user'),
				region: _tm.getDisplayValue('region'),
				role: _tm.getDisplayValue('role'),
				phone: _tm.getDisplayValue('user.phone'),
				order: order
			});
			order++;
		}
		return _tm_arr;
	},
	buildManagerShifts : function(start,region){
		var managers = [];
		var mr = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		mr.addEncodedQuery(`active=true^region=${region}^role=manager`);
		mr.query();
		while(mr.next()){
			managers.push({
				id: mr.getUniqueValue(),
				order: mr.order
			});
		}
		try {
			this.buildShifts(start,null,'manager',managers);
		} catch(err) {
			gs.info('Failed due to error: '+err);
		}

	},
	buildDraftShifts : function(start,team,role,roster){
		var shifts_draft = [];
		if(!start || !start.isValid()){
			start = new GlideDateTime();
		}
		if(!role){
			role = 'engineer';
		}
		if((!roster && team && role) || roster.length == 0){
			roster = this.buildTeamRoster(team,role);
		}

		try {	
			var ri = 0;
			var rotations = new GlideRecord(this.constants.TABLE_ROTATIONS);
			rotations.addEncodedQuery(`from<=javascript:gs.dateGenerate('${start}','start')^to>=javascript:gs.dateGenerate('${start}','end')^NQfrom>=javascript:gs.dateGenerate('${start}','end')`);
			rotations.orderBy('from');
			rotations.query();
			while(rotations.next()){
				var user = roster[ri];
				let shift = {
					rotation:rotations.getDisplayValue('rotation'),
					rotation_id: rotations.getUniqueValue(),
					team:team,
					team_member:user.name,
					team_member_id:user.id,
					type:role,
					from:rotations.getDisplayValue('from'),
					to:rotations.getDisplayValue('to')
				};
				//console.log(['shift',shift]);
				shifts_draft.push(shift);
				if(ri<roster.length-1){
					ri++;
				} else {
					ri = 0;
				}
			}
		} catch(err) {
			gs.info('Insertion failed due to error: '+err);

		}
		//console.log(shifts_draft);
		return shifts_draft;
	},
	buildShiftsFromDraft : function(draft){
		if(!draft){
			gs.info('No draft provided.');
			return;
		}
		try {	
			for(var i in draft){
				var ds = draft[i];
				let shift = new GlideRecord(this.constants.TABLE_SHIFTS);
				shift.initialize();
				shift.setValue('rotation',ds.rotation_id);
				shift.setValue('team',ds.team);			
				shift.setValue('team_member',ds.team_member_id);
				shift.setValue('type',ds.type);
				shift.setValue('from',ds.from);
				shift.setValue('to',ds.to);
				shift.insert();
			}

		} catch(err) {
			gs.info('Insertion failed due to error: '+err);

		}
	},
	buildShifts : function(start,team,role,roster,flag){
		if(!start || !start.isValid()){
			start = new GlideDateTime();
		}
		if(!role){
			role = 'engineer';
		}
		if((!roster && team && role) || roster.length == 0){
			roster = this.buildTeamRoster(team,role);
		}
		try {	
			var ri = 0;
			var rotations = new GlideRecord(this.constants.TABLE_ROTATIONS);
			//rotations.addEncodedQuery(`from>=javascript:gs.dateGenerate('${start}','end')`);
			rotations.addEncodedQuery(`from<=javascript:gs.dateGenerate('${start}','start')^to>=javascript:gs.dateGenerate('${start}','end')^NQfrom>=javascript:gs.dateGenerate('${start}','end')`);
			rotations.orderBy('from');
			rotations.query();
			while(rotations.next()){
				console.log(['rotations',ri,rotations.getDisplayValue('rotation')]);
				if(!this.checkDuplicateShift(rotations.getUniqueValue(),team,role)){
					var user = roster[ri];
						let shift = new GlideRecord(this.constants.TABLE_SHIFTS);
						shift.initialize();
						shift.setValue('rotation',rotations.getUniqueValue());
						shift.setValue('team',team);			
						shift.setValue('team_member',user.id);
						shift.setValue('type',role);
						shift.setValue('from',rotations.getValue('from'));
						shift.setValue('to',rotations.getValue('to'));
						shift.insert();
					if(ri<roster.length-1){
						ri++;
					} else {
						ri = 0;
					}
				} else {
					gs.info(`A duplicate shift already exists for ${rotations.getDisplayValue('rotation')} for ${team}`);
				}
			}
		} catch(err) {
			gs.info('Insertion failed due to error: '+err);

		}

	},
	/**
	 * CHECK & GET Helper Functions
	 * 
	 * checkDuplicateShift(Rotation sys_id, Team sys_id, Role string): checks Shift table for an existing shift of the same rotation & team
	 * returns: Boolean
	 * TODO: add flags for duplicate check-level (team, engineer)
	 * 
	 * getRotation(GlideDateTime): get sys_id of rotation during input date
	 * returns: Rotation sys_id
	 * 
	 * getManagersByRotation(Rotation sys_id, Region string): get array of Team Member records with role 'Manager' assigned to Shifts during given Rotation
	 * returns: Team Member array
	 *  
	 * getManagersByDate(GlideDate): uses getManagersByRotation to fetch assigned managers by date in case of unknown or unnecessary rotation (such as 'Today's On-shift Manager' widget idea)
	 * returns: Team Member array
	 * 
	 * getTeamShiftByDate(Team sys_id, GlideDate): uses getRotation and getTeamShiftByRotation to fetch team Shift by the current date (similar concept to getManagersByDate)
	 * returns: object built from Shift record
	 * TODO: Determine useage/deprecation
	 * 
	 * getTeamShiftByRotation(Team sys_id, Rotation sys_id): fetches single Shift record for provided Team and Rotation for uncancelled 'Engineer' Shifts
	 * returns: object built from Shift record
	 * TODO: add multiple record return
	 * 
	 * getAllShiftsForRotation(Rotation sys_id): fetches all Shift records for provided Rotation of uncancelled state and type 'Engineer'
	 * returns: Array of Shifts
	 * TODO: Allow role input?
	 * 
	 * getAssigneesByRotation(Rotation sys_id): fetches array of Team Member sys_ids from given Rotation. Used in BR and reference qualifier for use with email notification
	 * returns: Array of sys_id
	 * 
	 * getAllTeams(): fetches name & sys_id for all active Team records
	 * returns: Array of objects built from Team name and sys_id
	 * 
	 * getAllHolidaysForRotation(Rotation sys_id, Region string): fetches all Holidays records for provided Rotation and Region
	 * returns: Array of objects built from Holiday records
	 * 
	 * getReadonlyTable(Rotation sys_id): fetches Rotation, Shift, and Team Member information from provided Rotation and returns HTML table for use in email notification and widget
	 * returns: String of rendered HTML table
	 * 
	 * TODO: getTeamFromUser(User sys_id): fetches Team Member and then Team record from input sys_user User sys_id
	 * returns: Team sys_id
	 * TODO: build functionality
	 */
	checkDuplicateShift : function(rotation,team,role){
		var shift = new GlideRecord(this.constants.TABLE_SHIFTS);
		shift.addQuery('rotation',rotation);
		shift.addQuery('team',team);
		shift.addQuery('type',role);
		shift.query();
		if(shift.hasNext()){
			return true;
		}
		return false;
	},
	getRotation : function(date){
		const _date = date ?? new GlideDate();
		var rotation = new GlideRecord(this.constants.TABLE_ROTATIONS);
		rotation.addEncodedQuery(`from<=javascript:gs.dateGenerate('${_date}','start')^to>=javascript:gs.dateGenerate('${_date}','end')`);
		rotation.query();
		if(rotation.next()){
			return rotation.getUniqueValue();
		}
	},
	getManagersByRotation : function(rotation,region){
		var managers = [];
		var shifts = new GlideRecord(this.constants.TABLE_SHIFTS);
		shifts.addEncodedQuery(`rotation=${rotation}^type=manager^state!=cancelled`);
		if(region && region != ''){
			shifts.addQuery('team_member.region',region);
		}
		shifts.query();
		while(shifts.next()){
			let manager = {
				id:shifts.team_member.getValue('sys_id'),
				name:shifts.getDisplayValue('team_member'),
				user:shifts.team_member.user.getValue('sys_id'),
				email:shifts.getDisplayValue('team_member.user.email'),
				phone:shifts.getDisplayValue('team_member.user.phone'),
				shift_id:shifts.getUniqueValue(),
				region:shifts.getDisplayValue('team_member.region')
			};
			managers.push(manager);
		}
		return managers;
	},
	getManagersByDate : function(date){
		var _date = date ?? new GlideDate();
		var rotation = this.getRotation(_date);
		return this.getManagersByRotation(rotation);

	},
	getManagersByTeam : function(team){
		managers = [];
		var mr = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		mr.addQuery('assignment_group',team);
		mr.addQuery('role','manager');
		while(mr.next()){
			managers.push({
				id:mr.team_member.getValue('sys_id'),
				name:mr.getDisplayValue(),
				user:mr.user.getValue('sys_id'),
				email:mr.getDisplayValue('user.email'),
				phone:mr.getDisplayValue('user.phone'),
				region:mr.getDisplayValue('region')
			});
		}
		return managers;
	},
	getTeamShiftByDate : function(team,date){
		var _date = date ?? new GlideDate();
		var rotation = this.getRotation(_date);
		return this.getTeamShiftByRotation(team,rotation);
	},
	getTeamShiftByRotation : function(team,rotation){
		var shift = new GlideRecord(this.constants.TABLE_SHIFTS);
		shift.addEncodedQuery(`rotation=${rotation}^team=${team}^state!=cancelled^type=engineer`);
		shift.query();
		if(shift.next()){
			return {
				id:shift.team_member.getValue('sys_id'),
				team:shift.getDisplayValue('team'),
				name:shift.getDisplayValue('team_member'),
				email:shift.getDisplayValue('team_member.user.email'),
				phone:shift.getDisplayValue('team_member.user.phone'),
				shift_id:shift.getUniqueValue(),
				region:shift.getDisplayValue('team_member.region')
			};
		}
	},
	getAllShiftsForRotation : function(rotation){
		var engs = [];
		var shifts = new GlideRecord(this.constants.TABLE_SHIFTS);
		shifts.addEncodedQuery(`rotation=${rotation}^state!=cancelled^type=engineer`);
		shifts.query();
		while(shifts.next()){
			let eng = {
				id:shifts.team_member.getValue('sys_id'),
				team:shifts.getDisplayValue('team'),
				team_id:shifts.getValue('team'),
				name:shifts.getDisplayValue('team_member'),
				user:shifts.team_member.user.getValue('sys_id'),
				email:shifts.getDisplayValue('team_member.user.email'),
				phone:shifts.getDisplayValue('team_member.user.phone'),
				shift_id:shifts.getUniqueValue(),
				region:shifts.getDisplayValue('team_member.region')
			};
			engs.push(eng);
		}
		return engs;
	},
	getAssigneesByRotation : function(rotation) {
		var ids = [];
		var shifts = new GlideRecord(this.constants.TABLE_SHIFTS);
		shifts.addEncodedQuery('rotation='+rotation+'^state!=cancelled');
		shifts.query();
		while(shifts.next()){
			var user = shifts.getValue('team_member');
			ids.push(user);
		}
		return ids;
	},
	getAllTeams : function(){
		var teams = [];
		var tr = new GlideRecordSecure(this.constants.TABLE_TEAMS);
		tr.addQuery('active',true);
		tr.query();
		while(tr.next()){
			teams.push({team:tr.getDisplayValue(),team_id:tr.getUniqueValue()});
		}
		return teams;
	},
	getAllHolidaysForRotation : function(rotation,region){
		var _rotation = new GlideRecord(this.constants.TABLE_ROTATIONS);
		_rotation.get(rotation);
		let from = _rotation.getDisplayValue('from');
		let to = _rotation.getDisplayValue('to');

		var holidays = [];
		var hr = new GlideRecord(this.constants.TABLE_HOLIDAYS);
		hr.addEncodedQuery(`dateBETWEENjavascript:gs.dateGenerate('${from}','start')@javascript:gs.dateGenerate('${to}','end')`);
		if(region){
			hr.addQuery('region',region);
		}
		hr.orderBy('date');
		hr.query();
		while(hr.next()){
			let formatted_date = new GlideDate();
			formatted_date.setValue(hr.getDisplayValue('date'));
			formatted_date = formatted_date.getByFormat('MMM d');
			let holiday = {
				date:hr.getDisplayValue('date'),
				formatted_date:formatted_date,
				region:hr.getDisplayValue('region'),
				type:hr.getValue('type')
			};
			holidays.push(holiday);
		}
		return holidays;
	},
	getMarkdownTable : function(rotation) {
		var _rotation = new GlideRecord(this.constants.TABLE_ROTATIONS);
		var table = '';
		var newline = '\\n';
		if(_rotation.get(rotation)){
			table += '|'+_rotation.rotation +'|Name|Phone|'+newline;
			table += '|:-|:-:|:-:|'+newline;

			var managers = this.getManagersByRotation(rotation);

			if(managers.some(m => m.region == 'US')){
				var us_manager = managers.find(m=>m.region == 'US');
				table+='|US Manager|'+us_manager.name+'|'+us_manager.phone+'|';
			} else {
				table+='|US Manager|No manager scheduled||';
			}
			table += newline;

			if(managers.some(m => m.region == 'IDC')){
				var idc_manager = managers.find(m=>m.region == 'IDC');
				table+='|IDC Manager|'+idc_manager.name+'|'+idc_manager.phone+'|';
			} else {
				table+='|IDC Manager|No manager scheduled||';
			}
			table += newline;

			var shifts = this.getAllShiftsForRotation(rotation);
			for(var i=0;i < shifts.length; i++){
				var shift = shifts[i];
				table += '|'+shift.team+'|'+shift.name+'|'+shift.phone+'|' + newline;
				//table += newline;
			}

			var holidays_us = this.getAllHolidaysForRotation(rotation,'US');
			var holidays_idc = this.getAllHolidaysForRotation(rotation,'IDC');

			var us_wellness = holidays_us?.filter(h => h.type == 'wellness_day').map(h => h.formatted_date).join(', ') || 'None';

			var us_holidays = holidays_us?.filter(h => h.type == 'holiday').map(h => h.formatted_date).join(', ') || 'None';

			var idc_wellness = holidays_idc?.filter(h => h.type == 'wellness_day').map(h => h.formatted_date).join(', ') || 'None';

			var idc_holidays = holidays_idc?.filter(h => h.type == 'holiday').map(h => h.formatted_date).join(', ') || 'None';


			table += '|US Wellness Day|'+us_wellness+'||' + newline;
			table += '|US Holidays|'+us_holidays+'||' + newline;
			table += '|IDC Wellness Day|'+idc_wellness+'||' + newline;
			table += '|IDC Holidays|'+idc_holidays+'||' + newline;
		}
		return table;
	},
	getReadonlyTable : function(rotation) {
		var _rotation = new GlideRecord(this.constants.TABLE_ROTATIONS);
		_rotation.get(rotation);

		var managers = this.getManagersByRotation(rotation);
		var html = '<table class="rotation-table" id="rotation-table-readonly">';
		html += '<tr class="title-row"><th colspan="2">This Week: '+_rotation.rotation+'</th><th>Phone</th></tr>';

		html += '<tr class="manager-row"><th>US Manager</th>';
		if(managers.some(m => m.region == 'US')){
			html+='<td>'+managers.find(m=>m.region == 'US').name+'</td>';
		} else {
			html+='<td>No manager scheduled</td>';
		}
		if(managers.some(m => m.region == 'US')){
			html+='<td>'+managers.find(m=>m.region == 'US').phone+'</td></tr>';
		} else {
			html+='<td></td></tr>';
		}

		html += '<tr class="manager-row"><th>IDC Manager</th>';
		if(managers.some(m => m.region == 'IDC')){
			html+='<td>'+managers.find(m=>m.region == 'IDC').name+'</td>';
		} else {
			html+='<td>No manager scheduled</td>';
		}
		if(managers.some(m => m.region == 'IDC')){
			html+='<td>'+managers.find(m=>m.region == 'IDC').phone+'</td></tr>';
		} else {
			html+='<td></td></tr>';
		}

		var shifts = this.getAllShiftsForRotation(rotation);
		for(var i=0;i < shifts.length; i++){
			var shift = shifts[i];
			html+='<tr><th>'+shift.team+'</th><td>'+shift.name+'</td><td>'+shift.phone+'</td></tr>';
		}

		var holidays_us = this.getAllHolidaysForRotation(rotation,'US');
		var holidays_idc = this.getAllHolidaysForRotation(rotation,'IDC');

		var us_wellness = holidays_us?.filter(h => h.type == 'wellness_day').map(h => h.formatted_date).join(', ') || 'None';

		var us_holidays = holidays_us?.filter(h => h.type == 'holiday').map(h => h.formatted_date).join(', ') || 'None';

		var idc_wellness = holidays_idc?.filter(h => h.type == 'wellness_day').map(h => h.formatted_date).join(', ') || 'None';

		var idc_holidays = holidays_idc?.filter(h => h.type == 'holiday').map(h => h.formatted_date).join(', ') || 'None';


		html+= '<tr class="holiday-row"><th>US Wellness Day</th><td colspan="2">'+us_wellness+'</td></tr>';
		html += '<tr class="holiday-row"><th>US Holidays</th><td colspan="2">'+us_holidays+'</td></tr>';
		html += '<tr class="holiday-row"><th>IDC Wellness Day</th><td colspan="2">'+idc_wellness+'</td></tr>';
		html +=	'<tr class="holiday-row"><th>IDC Holidays</th><td colspan="2">'+idc_holidays+'</td></tr>';
		html += '</table>';
	return html;
	},
	getMemberFromUser: function(user){
		var tm = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		tm.addQuery('user',user);
		tm.query();
		if(tm.next()){
			return tm;
		}
		return;
	},
	getTeamFromUser: function(user){
		var tm = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		tm.addQuery('user',user);
		tm.query();
		if(tm.next()){
			return tm.getValue('assignment_group');
		}
		return;

	},
	getManagedTeams: function(user){
		var teams = [];
		var tm = new GlideRecord(this.constants.TABLE_TEAMS);
		tm.addQuery('group_owner',user);
		tm.query();
		while(tm.next()){
			teams.push(tm.getValue('sys_id'));
		}
		gs.info(teams);
		return teams.join(',');
	},
	getNextOrderNumber: function(team){
		gs.info('Team: '+team);
		var tm = new GlideRecord(this.constants.TABLE_TEAM_MEMBERS);
		tm.addNotNullQuery('order');
		tm.addQuery('assignment_group',team);
		tm.orderByDesc('order');
		tm.setLimit(1);
		tm.query();
		if(tm.next()){
			gs.info('Highest current order is '+tm.getValue('order'));
			return parseInt(tm.getValue('order')) + 1;
		} else {
			return 0;
		}

	},
	type: 'OnCallUtils'
});
