sysauto_script.script:
gs.info('Kicking off weekly on-call schedule');
var ocu = new x_snc_on_call_sc_0.OnCallUtils();

var rotation_id = ocu.getRotation(new GlideDateTime());

var rotation = new GlideRecord(ocu.getConstants().TABLE_ROTATIONS);
if(rotation.get(rotation_id)){
	var team_members = rotation.getValue('assignees');
	gs.info(['team members',team_members]);
	var users_arr = []; // get sys_user sys_ids for email notification
	var mm_id_arr = []; // get sys_user user ids for mattermost cc

	var team_arr = team_members.split(',');
	for(u in team_arr){
		let u_id = ocu.getUserFromMember(team_arr[u]);
		users_arr.push(u_id);

		var mm_id = new GlideRecord('sys_user');
		if(mm_id.get(u_id)){
			mm_id_arr.push('@'+mm_id.getValue('user_name'));
		}
	}

	var users_str = users_arr.join(',');
	gs.info(users_str);
	gs.eventQueue('x_snc_on_call_sc_0.rotation.started', rotation, users_str);

	gs.info(mm_id_arr);
	var cc_text = 'CC: '+mm_id_arr.join(', ');
	gs.info(cc_text);

	var markdown_table = ocu.getMarkdownTable(rotation_id);

	var msg = cc_text + '\\n\\n' + markdown_table;
	gs.info([gs.getProperty('glide.servlet.uri'),msg]);
} else {
	gs.error('No rotation found for this week, aborting execution.');
}
